import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';

@Component({
  selector: 'app-dish',
  templateUrl: './dish.component.html',
  styleUrls: ['./dish.component.css']
})
export class DishComponent implements OnInit{


  dishes=[
    {
      "id": 1,
      "dish": "Tikka Masala",
      "ingredients": [
        "olive oil",
        "chicken",
        "onion",
        "cloves garlic",
        "ginger",
        "jalapeno",
        "turmeric",
        "cumin",
        "paprika",
        "masala",
        "cayenne pepper",
        "tomatoes",
        "heavy cream",
        "Kosher salt",
        "cilantro",
        "rice",
        "naan"
      ],
      "price": 5,
      "pic": "assets/pics/tikka-masala.png"
    },
    {
      "id": 2,
      "dish": "Truffled Pizza",
      "ingredients": [
        "dough",
        "shiitake mushroom",
        "cremini mushrooms",
        "portobello mushrooms",
        "fontina cheese",
        "thyme",
        "truffle oil",
        "parmesan cheese"
      ],
      "price": 5,
      "pic": "assets/pics/truffled-pizza.png"
    },
    {
      "id": 3,
      "dish": "Mujadara",
      "ingredients": [
        "olive oil",
        "onion",
        "rice",
        "garlic",
        "lentils",
        "cumin",
        "salt"
      ],
      "price": 5,
      "pic": "assets/pics/mujadara.png"
    },
    {
      "id": 4,
      "dish": "Meat Lover Burger",
      "ingredients": [
        "bun",
        "onion",
        "ground beef",
        "special sauce",
        "bacon"
      ],
      "price": 5,
      "pic": "assets/pics/meat-burger.png"
    }];
    dish: any;
  @Input() parentDishId: any;  
  @Output() sumEvent = new EventEmitter();
  input = "helo compo";
  ngOnInit(): void {
    for(let i=0; i<this.dishes.length; i++){
      if(this.parentDishId === this.dishes[i].id){
        this.dish = this.dishes[i];
        return;
      }
    }
  }
  buttonClick = (value: any)=>{
    let sum = value * this.dish.price;
    console.log('This is value from child: ',sum);
    this.sumEvent.emit(sum);
  }


}
<p>dish works!</p>
<h1>Dish name: {{dish.dish}}</h1>
<p>Price: {{dish.price}}</p>
<input type="text" [value]="input" #inputVal> 
{{inputVal.value}}
<button (click)="buttonClick(inputVal.value)">Cliock me!</button>
