@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit, OnDestroy{
  recipeName = 'Biriyani';
  dishId = 3;
  total: any;
  constructor(){

  }
  ngOnDestroy(): void {

  }
  ngOnInit(): void {
    // alert('Hello')
  }
  receiveSum(sum: any){
this.total = sum;
console.log('Total value from child is: ', this.total)
  }
}
<p>home works!</p>
<p>The recipe is: {{ recipeName }}</p>
